import React, { useReducer } from "react";
import CartContext from "./cart-context";

const addItemFromCartHandler = (item) => {
  dispatchCarAction({ type: "ADD", item: item });
};
const removeItemFromCartHandler = (id) => {
  dispatchCarAction({ type: "REMOVE", id: id });
};
const cartContext = {
  item: cartState.items,
  totalAmount: cartState.totalAmount,
  addItem: addItemFromCartHandler,
  removeItem: removeItemFromCartHandler,
};

const defaultCartState = {
  items: [],
  totalAmount: 0,
};
const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedItems = state.items.concat(action.item);
    const updatedTotalAmount =
      state.item.totalAmount + state.item.price * state.item.amount;
    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCarAction] = useReducer(
    cartReducer,
    defaultCartState
  );
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
